import { Helmet } from 'react-helmet-async';
import PageTitle from 'src/components/PageTitle';
import PageTitleWrapper from 'src/components/PageTitleWrapper';
import { Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import { useState } from 'react';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Footer from 'src/components/Footer';
import Input from '@mui/material/Input';

import TextField from '@mui/material/TextField';
function Forms() {

  const [currency, setCurrency] = useState('EUR');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };

  const [value, setValue] = useState(30);

  const handleChange2 = (event, newValue) => {
    setValue(newValue);
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

function TabsDemo() {

  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <>
      <Helmet>
        <title>Tabs - Components</title>
      </Helmet>
      <PageTitleWrapper>
        <PageTitle
          heading="Router"
        //  subHeading="Tabs make it easy to explore and switch between different views."
          />
      </PageTitleWrapper>
      <Container maxWidth="lg">
        <Grid
          container
          direction="row"
          justifyContent="center"
          alignItems="stretch"
          spacing={3}
        >
          <Grid item xs={12}>
                <Box sx={{ width: '100%' }}>
                  <Tabs variant="scrollable"
                    scrollButtons="auto"
                    textColor="primary"
                    indicatorColor="primary" value={value} onChange={handleChange} aria-label="basic tabs example">
                    <Tab label="Initial settings" {...a11yProps(0)} />
                    <Tab label="Interfaces" {...a11yProps(1)} />
                    <Tab label="Item Three" {...a11yProps(2)} />
                  </Tabs>
                  <TabPanel value={value} index={0}>
                    <Card>
                    <CardHeader title="Basale router indstillinger" />
                    <Divider />
                    <CardContent>
                  <Box
                    component="form"
                    sx={{
                      '& .MuiTextField-root': { m: 1, width: '25ch' },
                    }}
                    noValidate
                    autoComplete="off"
                  >
                    <div>
                      <TextField
                        required
                        id="router.initial.hostname.input"
                        label="Hostname"
                        defaultValue={localStorage.router.initial.hostname.input}
                        placeholder="R1"
                        onChange={(event) => {localStorage.router.initial.hostname.input = event.target.value}}
                        //inputRef={textFieldRef}
                      />
                      <TextField
                        id="router.initial.motd.input"
                        label="MOTD"
                        //defaultValue="R1"
                        placeholder="Authorized access only!"
                      />
                      <TextField
                        id="router.initial.domæne.input"
                        label="Domæne"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.secret.input"
                        label="Enable secret"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.con0pass.input"
                        label="Con 0 password"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.vtypass.input"
                        label="Vty 0-15 password"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                    </div>
                  </Box>
                  </CardContent>
                  </Card>
                  </TabPanel>
                  <TabPanel value={value} index={1}>
                    Item Two
                  </TabPanel>
                  <TabPanel value={value} index={2}>
                    Item Three
                  </TabPanel>
                </Box>


          </Grid>
        </Grid>
      </Container>
      <Footer />
    </>
  );
}

export default TabsDemo;
