import { Helmet } from 'react-helmet-async';
import PageTitle from 'src/components/PageTitle';
import PageTitleWrapper from 'src/components/PageTitleWrapper';
import { Dialog, DialogTitle, Alert, Button, Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import { useState } from 'react';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Footer from 'src/components/Footer';
import Input from '@mui/material/Input';



import * as React from 'react';
import Modal from '@mui/material/Modal';



import TextField from '@mui/material/TextField';
function Forms() {

  const [currency, setCurrency] = useState('EUR');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };

  const [value, setValue] = useState(30);

  const handleChange2 = (event, newValue) => {
    setValue(newValue);
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

function TabsDemo() {

  const [value, setValue] = useState(0);

//  const handleChange = (event, newValue) => {
//    setValue(newValue);
//  };

  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
  const [Motd, setMotd] = useState(localStorage.router_initial_motd_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);

const [open, setOpen] = React.useState(false);
const handleOpen = () => setOpen(true);
const handleClose = () => setOpen(false);
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};


const Start = () => {
		var today = new Date()
		var workingvar = ""
    if (true == true) {workingvar += "clock set " + today.getHours()+":"+today.getMinutes()+":"+today.getSeconds()+" "+today.getDate()+" "+today.toLocaleString('en-us', { month: 'short' })+" "+today.getFullYear()}
    workingvar +=  "\nconfigure terminal"
    workingvar += "\nset hostname " + localStorage.router_initial_hostname_input
    localStorage.router_initial_final = workingvar; return workingvar
  }



  return (
    <>
      <Helmet>
        <title>Router</title>
      </Helmet>
      <PageTitleWrapper>
        <PageTitle
          heading="Router"
 //subHeading="Rouer konfiguration."
          />
      </PageTitleWrapper>
      <Container maxWidth="lg">
        <Grid
          container
          direction="row"
          justifyContent="center"
          alignItems="stretch"
          spacing={3}
        >
          <Grid item xs={12}>
                <Box sx={{ width: '100%' }}>
                  <Tabs variant="scrollable"
                    scrollButtons="auto"
                    textColor="primary"
                    indicatorColor="primary" value={value} onChange={(event, newValue) => {setValue(newValue)}} aria-label="basic tabs example">
                    <Tab label="Initial settings" {...a11yProps(0)} />
                    <Tab label="Interfaces" {...a11yProps(1)} />
                    <Tab label="Item Three" {...a11yProps(2)} />
                    <Tab label="Item Four" {...a11yProps(3)} />
                  </Tabs>
                  <TabPanel value={value} index={0}>
                    <Card>
                    <CardHeader title="Basale router indstillinger" />
                    <Divider />
                    <CardContent>
                  <Box
                    component="form"
                    sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                    }}
                    noValidate
                    autoComplete="off"
                  >
                    <div>
                      <TextField
                        required
                        id="router.initial.hostname.input"
                        label="Hostname"
                        defaultValue={localStorage.router_initial_hostname_input}
                        value = {Hostname}
                        placeholder="R1"
                        onChange={(event) => {localStorage.router_initial_hostname_input = event.target.value; setHostname(event.target.value)}}
                        //inputRef={textFieldRef}
                      />
                      <TextField
                        id="router_initial_motd_input"
                        label="MOTD"
                        value = {Motd}
                        defaultValue={localStorage.router_initial_motd_input}
                        Typographyplaceholder="Authorized access only!"
                        onChange={(event) => {localStorage.router_initial_motd_input = event.target.value; setMotd(event.target.value)}}
                      />
                      <TextField
                        id="router.initial.domæne.input"
                        label="Domæne"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.secret.input"
                        label="Enable secret"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.con0pass.input"
                        label="Con 0 password"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                      <TextField
                        id="router.initial.vtypass.input"
                        label="Vty 0-15 password"
                        //defaultValue="R1"
                        placeholder="domain.internal"
                      />
                    </div>
                    <Button variant="outlined" color="error" sx={{ margin: 1 }} size="medium" onClick={() =>{
                    localStorage.router_initial_motd_input = "" ; setMotd("")
                    localStorage.router_initial_hostname_input = "" ; setHostname("")
                  //  alert(Hostname)
                  }}
                    >
                      Ryd felter
                    </Button>
                    <Button onClick={handleOpen} variant="outlined" sx={{ margin: 1 }} size="medium" color="success">
                      Vis config
                    </Button>
                      <Modal
                          open={open}
                          onClose={handleClose}
                          aria-labelledby="modal-modal-title"
                          aria-describedby="modal-modal-description"
                          >
                          <Box sx={style}>
                          <Typography id="modal-modal-title" variant="h6" component="h2">
                          KOnfig
                          </Typography>
                          <Typography style={{wordWrap: "break-word", color:"#FFC13D"}} id="modal-modal-description" sx={{ mt: 2 }}>
                          {Start()}
                          </Typography>
                          <Button onClick={() => {navigator.clipboard.writeText(localStorage.router_initial_final)}} variant="outlined" sx={{right: '20%',left: '20%', margin: 2 }} size="small" color="success">
                            Kopier til udklipsholder
                          </Button>
                          </Box>
                      </Modal>
                  </Box>
                  </CardContent>
                  </Card>
                  </TabPanel>
                  <TabPanel value={value} index={1}>
                    Item Two
                  </TabPanel>
                  <TabPanel value={value} index={2}>
                    Item Three
                  </TabPanel>
                  <TabPanel value={value} index={3}>
                    Item four
                  </TabPanel>
                </Box>


          </Grid>
        </Grid>
      </Container>
      <Footer />
    </>
  );
}

export default TabsDemo;
