import { Helmet } from 'react-helmet-async';
import PageTitle from 'src/components/PageTitle';
import PageTitleWrapper from 'src/components/PageTitleWrapper';
import { Dialog, DialogTitle, Alert, Button, Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import { useState } from 'react';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Footer from 'src/components/Footer';
import Input from '@mui/material/Input';



import * as React from 'react';
import Modal from '@mui/material/Modal';



import TextField from '@mui/material/TextField';
function Forms() {

  const [currency, setCurrency] = useState('EUR');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };

  const [value, setValue] = useState(30);

  const handleChange2 = (event, newValue) => {
    setValue(newValue);
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

function TabsDemo() {

  const [value, setValue] = useState(0);

//  const handleChange = (event, newValue) => {
//    setValue(newValue);
//  };

  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
  const [Motd, setMotd] = useState(localStorage.router_initial_motd_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);
//  const [Hostname, setHostname] = useState(localStorage.router_initial_hostname_input);

const [open, setOpen] = React.useState(false);
const handleOpen = () => setOpen(true);
const handleClose = () => setOpen(false);
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};
const styles = {
  input: {
    "&:invalid": {
      border: "red solid 2px"
    }
  }
};


























const [formFields, setFormFields] = useState([{ "name": '', "age": '' }])
//const [formFields, setFormFields] = useState(localStorage.router_interfaces_array)

const handleFormChange = (event, index) => {
//  let data = [...formFields]
  let data = JSON.parse(JSON.stringify(formFields));
  data[index][event.target.name] = event.target.value;
  setFormFields(data);
  localStorage.router_interfaces_array = JSON.stringify(data)
}

const submit = (e) => {
  e.preventDefault();
  console.log(formFields)
}

const addFields = () => {
  let object = {
    name: '',
    age: ''
  }

  setFormFields([...formFields, object])
}

const removeFields = (index) => {
  let data = [...formFields];
  data.splice(index, 1)
  setFormFields(data)
  localStorage.router_interfaces_array = JSON.stringify(data)
}
































const Start = () => {
		var today = new Date()
		var workingvar = ""
    if (true == true) {workingvar += "clock set " + today.getHours()+":"+today.getMinutes()+":"+today.getSeconds()+" "+today.getDate()+" "+today.toLocaleString('en-us', { month: 'short' })+" "+today.getFullYear()}
    workingvar +=  "\nconfigure terminal"
